import { innerFrom } from 'rxjs/src/internal/observable/innerFrom';
import { observeOn } from 'rxjs/src/internal/operators/observeOn';
import { subscribeOn } from 'rxjs/src/internal/operators/subscribeOn';
import { InteropObservable, SchedulerLike } from 'rxjs/src/internal/types';

export function scheduleObservable<T>(input: InteropObservable<T>, scheduler: SchedulerLike) {
  return innerFrom(input).pipe(subscribeOn(scheduler), observeOn(scheduler));
}
