import { SchedulerLike, ReadableStreamLike } from 'rxjs/src/internal/types';
import { Observable } from 'rxjs/src/internal/Observable';
import { scheduleAsyncIterable } from 'rxjs/src/internal/scheduled/scheduleAsyncIterable';
import { readableStreamLikeToAsyncGenerator } from 'rxjs/src/internal/util/isReadableStreamLike';

export function scheduleReadableStreamLike<T>(input: ReadableStreamLike<T>, scheduler: SchedulerLike): Observable<T> {
  return scheduleAsyncIterable(readableStreamLikeToAsyncGenerator(input), scheduler);
}
