import { InteropObservable } from 'rxjs/src/internal/types';
import { observable as Symbol_observable } from 'rxjs/src/internal/symbol/observable';
import { isFunction } from 'rxjs/src/internal/util/isFunction';

/** Identifies an input as being Observable (but not necessary an Rx Observable) */
export function isInteropObservable(input: any): input is InteropObservable<any> {
  return isFunction(input[Symbol_observable]);
}
